package practice.interview.preparation.kit.string.manipulation

import spock.lang.Specification
import spock.lang.Unroll

import static practice.interview.preparation.kit.string.manipulation.SherlockAndTheValidStringSolution.isValid

class SherlockAndTheValidStringSolutionTest extends Specification {

    @Unroll
    def 'testInputString'() {
        expect:
        result.equals(isValid(input))

        where:
        input                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      || result
        'abc'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      || 'YES'
        'abccc'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    || 'NO'
        'abab'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     || 'YES'
        'aabbcd'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   || 'NO'
        'aabbccddeefghi'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           || 'NO'
        'aaaaaaaabbb'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              || 'NO'
        'abcdefghhgfedecba'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        || 'YES'
        '' || 'YES'
    }
}
